import React from 'react'

import { Link } from 'react-router-dom'

import './sidebar.css'

import logo from '../../assets/images/LEDYLOGO.png'

import sidebar_items from '../../assets/JsonData/sidebar_routes.json'

import sidebar_items_owner from '../../assets/JsonData/sidebar_routes.json'


const SidebarItem = props => {

    const active = props.active ? 'active' : ''

    return (
        <div className="sidebar__item">
            <div className={`sidebar__item-inner ${active}`}>
                <i className={props.icon}></i>
                <span>
                    {props.title}
                </span>
            </div>
        </div>
    )
}

const Sidebar = props => {
    let role = "waiter"
    let items =[];
    if(role=="waiter"){
        items=sidebar_items
    }else if(role=="owner"){
        items=sidebar_items_owner
    }

    const activeItem = items.findIndex(item => item.route === props.location.pathname)
    const activeItemOwner = items.findIndex(item=>item.route===props.location.pathname)
    return (
        <div className='sidebar'>
            <div className="sidebar__logo">
                <img src={logo} alt="company logo" />
            </div>
            {
                items.map((item, index) => (
                    <Link to={item.route} key={index}>
                        <SidebarItem
                            title={item.display_name}
                            icon={item.icon}
                            active={index === activeItem}
                        />
                    </Link>
                ))
            }
        </div>
    )
}

export default Sidebar
