import React, { useState } from "react";
import { ethers } from "ethers";
import { WalletLinkConnector } from "@web3-react/walletlink-connector";
import { WalletConnectConnector } from "@web3-react/walletconnect-connector";
import { InjectedConnector } from "@web3-react/injected-connector";
import { useWeb3React } from "@web3-react/core";

const CoinbaseWallet = new WalletLinkConnector({
  url: `https://mainnet.infura.io/v3/${process.env.INFURA_KEY}`,
  appName: "Web3-react Demo",
  supportedChainIds: [1, 3, 4, 5, 42],
});
const WalletConnect = new WalletConnectConnector({
  rpcUrl: `https://mainnet.infura.io/v3/${process.env.INFURA_KEY}`,
  bridge: "https://bridge.walletconnect.org",
  qrcode: true,
});
const Injected = new InjectedConnector({
  supportedChainIds: [1, 3, 4, 5, 42],
});
const Finance = () => {
  const [error, seterror] = useState("");
  const [defaultAccount, setDefaultAccount] = useState();
  const [userBalance, setuserBalance] = useState();
  const { activate, deactivate } = useWeb3React();
  const { library } = useWeb3React();

  const switchNetwork = async () => {
    try {
      await library.provider.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x63564c40" }],
      });
    } catch (switchError) {
      // 4902 error code indicates the chain is missing on the wallet
      if (switchError.code === 4902) {
        try {
          await library.provider.request({
            method: "wallet_addEthereumChain",
            params: [
              {
                chainId: "0x63564c40",
                rpcUrls: ["https://api.harmony.one"],
                chainName: "Harmony Mainnet",
                nativeCurrency: { name: "ONE", decimals: 18, symbol: "ONE" },
                blockExplorerUrls: ["https://explorer.harmony.one"],
                iconUrls: [
                  "https://harmonynews.one/wp-content/uploads/2019/11/slfdjs.png",
                ],
              },
            ],
          });
        } catch (error) {
          console.error(error);
        }
      }
    }
  };
  // retrieve provider and connect when component mounts
  useEffect(() => {
    const provider = window.localStorage.getItem("provider");
    if (provider) activate(connectors[provider]);
  }, []);
  const openMetamask = () => {
    if (window.ethereum) {
      window.ethereum.request({ method: "eth_requestAccounts" }).then((res) => {
        accounChangeHandler(res[0]);
      });
    } else {
      seterror("install metamask");
    }
  };
  const accounChangeHandler = (newAccount) => {
    setDefaultAccount(newAccount);
    getUserBalance(newAccount);
  };
  const getUserBalance = (address) => {
    window.ethereum
      .request({ method: "eth_getBalance", params: [address, "latest"] })
      .then((balance) => {
        setuserBalance(ethers.utils.formatEther(balance));
      });
  };
  //window.ethereum.on("accountsChanged", accounChangeHandler);
  return (
    <div className="card">
      <h2>Finance</h2> <br />
      <div class="wallet">
        <aside class="left-wallet">
          <div class="wallet-head">
            <h1> My Wallets </h1>
            <div class="modal-open" onClick={openMetamask}>
              +
            </div>
          </div>
          '
          <div className="col-8">
            <button
              className="button col-12"
              onClick={() => {
                activate(CoinbaseWallet);
              }}
            >
              Coinbase Wallet
            </button>
            <br />
            <br />

            <button
              className="button col-12"
              onClick={() => {
                activate(WalletConnect);
              }}
            >
              Wallet Connect
            </button>
            <br />
            <br />

            <button
              className="button col-12"
              onClick={() => {
                activate(Injected);
              }}
            >
              Metamask
            </button>
            <br />
            <br />
            <button className="button col-12" onClick={deactivate}>
              Disconnect
            </button>
          </div>
          <div class="cc-select">
            <div id="'+data.id+'" class="cc mc">
              <div class="cc-img-main"></div>
              <div class="cc-num">{defaultAccount}</div>
              <div class="cc-date">Valid Thru: 12/17</div>
            </div>
            "
          </div>
        </aside>
        <content className="right-trans">
          <h1> Current Balance </h1>
          <h4 id="balance">
            {userBalance} ETH
            {error}
          </h4>
          <div class="trans-list">
            '{" "}
            <div class="trans trans mc">
              <div class="trans-details">
                <span class="trans-plus"></span>
                <h3 class="trans-name">Apple Iphone 6, 6g GB</h3>
                <h5 class="trans-type-date">
                  Electronics #343223 - 12 July, 2015
                </h5>
              </div>
              <div class="trans-amt">
                <h4 class="trans-amt amt-green">5000 DT</h4>
              </div>
            </div>
          </div>
        </content>
      </div>
    </div>
  );
};

export default Finance;
