import React from "react";
import axios from "axios";
import { BaseUrl } from "../../helpers/base_url";
import { useState } from "react";
import GoogleMapReact from 'google-map-react';
const AnyReactComponent = ({ text }) => <div>{text}</div>;
const Resto = () => {
  const [openEdit, setopenEdit] = useState(false);
  const [dataReady, setdataReady] = useState(false);
  const [restoData,setrestoData] = useState({
    name : "",
    description : "",
    location : [],
    speciality : "",
    image : ""
  });
  const openEditRestofore = async (restaurant) => {
    setopenEdit(false);
    await setrestoData(restaurant);
    setopenEdit(true);
  };
  
  
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/restaurant/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        //   console.log(response.data);
        if(response.status == 200)
        {
          setdataReady(true);
          setrestoData(response.data);
        }
        

      }).catch((error) => {
        console.log(error);
      });
  }, []);
  
  return(
    <div>
       {dataReady ? (
                
                 <div>
                   <button onClick={() => openEditRestofore(restoData)}>Edit</button>
                   <div> { restoData.name} </div>
                   <br></br>
                   <div> { restoData.description} </div>
                   <br></br>
                   <div> { restoData.speciality} </div>
                   <br></br>
                  <img src={ restoData.image}  style={{height:'auto',width:'35%'}} />
                  <br></br>
                    
                     </div>
                    

                ) : (
                  <div></div>
                )}
                 <div>
                   <h3>Edit Restaurant</h3>
                   <form>
                     <div>
                       <input type="text" name="name" value={this.state.name} placeholder="name" />
                     </div>
                     <div>
                       <input type="text" name="description" value={this.state.description} placeholder="description" />
                     </div>
                     <div>
                       <input type="text" name="speciality" value={this.state.speciality} placeholder="speciality" />
                     </div>
                     </form>
                 </div>
    </div>
    
  )

};

export default Resto;
