import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import { Link } from "react-router-dom";

import Chart from "react-apexcharts";

import axios from "axios";
import { BaseUrl } from "../helpers/base_url";
import { useSelector } from "react-redux";

import StatusCard from "../components/status-card/StatusCard";

import Table from "../components/table/Table";

import Badge from "../components/badge/Badge";

import statusCards from "../assets/JsonData/status-card-data.json";

const chartOptions = {
  series: [
    {
      name: "Online Customers",
      data: [40, 70, 20, 90, 36, 80, 30, 91, 60],
    },
    {
      name: "Store Customers",
      data: [40, 30, 70, 80, 40, 16, 40, 20, 51, 10],
    },
  ],
  options: {
    color: ["#6ab04c", "#2980b9"],
    chart: {
      background: "transparent",
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      curve: "smooth",
    },
    xaxis: {
      categories: [
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "Jun",
        "Jul",
        "Aug",
        "Sep",
      ],
    },
    legend: {
      position: "top",
    },
    grid: {
      show: false,
    },
  },
};

const topCustomers = {
  head: ["user", "total orders", "total spending"],
  body: [
    {
      username: "john doe",
      order: "490",
      price: "$15,870",
    },
    {
      username: "frank iva",
      order: "250",
      price: "$12,251",
    },
    {
      username: "anthony baker",
      order: "120",
      price: "$10,840",
    },
    {
      username: "frank iva",
      order: "110",
      price: "$9,251",
    },
    {
      username: "anthony baker",
      order: "80",
      price: "$8,840",
    },
  ],
};

const renderCusomerHead = (item, index) => <th key={index}>{item}</th>;

const renderCusomerBody = (item, index) => (
  <tr key={index}>
    <td>{item.username}</td>
    <td>{item.order}</td>
    <td>{item.price}</td>
  </tr>
);

const latestOrders = {
  header: ["order id", "user", "total price", "date", "status"],
  body: [
    {
      id: "#OD1711",
      user: "john doe",
      date: "17 Jun 2021",
      price: "$900",
      status: "shipping",
    },
    {
      id: "#OD1712",
      user: "frank iva",
      date: "1 Jun 2021",
      price: "$400",
      status: "paid",
    },
    {
      id: "#OD1713",
      user: "anthony baker",
      date: "27 Jun 2021",
      price: "$200",
      status: "pending",
    },
    {
      id: "#OD1712",
      user: "frank iva",
      date: "1 Jun 2021",
      price: "$400",
      status: "paid",
    },
    {
      id: "#OD1713",
      user: "anthony baker",
      date: "27 Jun 2021",
      price: "$200",
      status: "refund",
    },
  ],
};

const orderStatus = {
  shipping: "primary",
  pending: "warning",
  paid: "success",
  refund: "danger",
};

const renderOrderHead = (item, index) => <th key={index}>{item}</th>;

const renderOrderBody = (item, index) => (
  <tr key={index}>
    <td>{item.id}</td>
    <td>{item.user}</td>
    <td>{item.price}</td>
    <td>{item.date}</td>
    <td>
      <Badge type={orderStatus[item.status]} content={item.status} />
    </td>
  </tr>
);

const Dashboard = () => {
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalOrders, settotalOrders] = useState(0);
  const [dataReady, setdataReady] = useState(false);
  const [numberCompletedOrders, setnumberCompletedOrders] = useState(0);
  const history = useHistory();
  if (localStorage.getItem("auth") != "true") {
    //check condition
    history.push("/login");
  }
  const themeReducer = useSelector((state) => state.ThemeReducer.mode);
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/command/getall/byrestaurantid/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        console.log(response.data);
        let money = 0;
        let totalsales = 0;
        response.data.forEach((element) => {
          if (element.etat == "Completed") {
            console.log("salem");
            totalsales++;
            money = money + element.price;
          }
        });
        settotalOrders(response.data.length);
        setTotalIncome(money);
        setnumberCompletedOrders(totalsales);
        // setlistOrder(response.data);
        setdataReady(true);
      });
  });
  return (
    <div>
      <h2 className="page-header">Dashboard</h2>
      <div className="row">
        {dataReady ? (
          <div className="col-6">
            <div className="row">
              <div className="col-6" key={1}>
                <StatusCard
                  icon="bx bx-dollar-circle"
                  count={"$" + totalIncome}
                  title="Total income"
                />
              </div>
              <div className="col-6" key={2}>
                <StatusCard
                  icon="bx bx-receipt"
                  count={totalOrders}
                  title="Total orders"
                />
              </div>
              <div className="col-6" key={3}>
                <StatusCard
                  icon="bx bx-shopping-bag"
                  count={numberCompletedOrders}
                  title="Total sales"
                />
              </div>
              <div className="col-6" key={2}>
                <StatusCard
                  icon="bx bx-dollar-circle"
                  count="$2,632"
                  title="Total income"
                />
              </div>
            </div>
          </div>
        ) : (
          <div></div>
        )}

        <div className="col-6">
          <div className="card full-height">
            {/* chart */}
            <Chart
              options={
                themeReducer === "theme-mode-dark"
                  ? {
                      ...chartOptions.options,
                      theme: { mode: "dark" },
                    }
                  : {
                      ...chartOptions.options,
                      theme: { mode: "light" },
                    }
              }
              series={chartOptions.series}
              type="line"
              height="100%"
            />
          </div>
        </div>
        <div className="col-4">
          <div className="card">
            <div className="card__header">
              <h3>top customers</h3>
            </div>
            <div className="card__body">
              <Table
                headData={topCustomers.head}
                renderHead={(item, index) => renderCusomerHead(item, index)}
                bodyData={topCustomers.body}
                renderBody={(item, index) => renderCusomerBody(item, index)}
              />
            </div>
            <div className="card__footer">
              <Link to="/">view all</Link>
            </div>
          </div>
        </div>
        <div className="col-8">
          <div className="card">
            <div className="card__header">
              <h3>latest orders</h3>
            </div>
            <div className="card__body">
              <Table
                headData={latestOrders.header}
                renderHead={(item, index) => renderOrderHead(item, index)}
                bodyData={latestOrders.body}
                renderBody={(item, index) => renderOrderBody(item, index)}
              />
            </div>
            <div className="card__footer">
              <Link to="/">view all</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
