import React from "react";
import axios from "axios";
import { BaseUrl } from "../../helpers/base_url";
import { useState } from "react";
import GoogleMapReact from 'google-map-react';
const AnyReactComponent = ({ text }) => <div>{text}</div>;
const Resto = () => {
  const [openEdit, setopenEdit] = useState(false);
  const [dataReady, setdataReady] = useState(false);
  const [restoData,setrestoData] = useState({
    name : "",
    description : "",
    location : [],
    speciality : "",
    image : ""
  });
  const openEditRestofore = async (restaurant) => {
    setopenEdit(false);
    await setrestoData(restaurant);
    setopenEdit(true);
  };
  
  
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/restaurant/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        //   console.log(response.data);
        if(response.status == 200)
        {
          setdataReady(true);
          setrestoData(response.data);
        }
        

      }).catch((error) => {
        console.log(error);
      });
  }, []);
  
  return(
    <div>
       {dataReady ? (
                
                 <div>
                   <button onClick={() => openEditRestofore(restoData)}>Edit</button>
                   <div> { restoData.name} </div>
                   <br></br>
                   <div> { restoData.description} </div>
                   <br></br>
                   <div> { restoData.speciality} </div>
                   <br></br>
                  <img src={ restoData.image}  style={{height:'auto',width:'35%'}} />
                  <br></br>
                    
                     </div>
                    

                ) : (
                  <div></div>
                )}
               {openEdit ? (
                 <div className="col-12">
                 <br />

                 <label>Meal name</label>
                 <br />
                 <br />
                 <Input
                   type="text"
                   placeholder="Meal name"
                   value={mealtoAdd["name"]}
                   onChange={(e) => setmealtoAdd({ name: e.target.value })}
                 />
                 <br />

                 <label>Meal description</label>
                 <br />
                 <br />
                 <Input
                   type="text"
                   placeholder="Meal description"
                   value={mealtoAdd.description}
                   onChange={(e) =>
                     setmealtoAdd({ description: e.target.value })
                   }
                 />

                 <label>
                   <br />

                   <label>Meal price</label>
                   <br />
                   <br />
                   <Input
                     type="number"
                     placeholder="Meal price"
                     value={mealtoAdd.price}
                     onChange={(e) =>
                       setmealtoAdd({ price: e.target.value })
                     }
                   />
                 </label>
                 <br />

                 <label>Meal duration</label>
                 <br />
                 <br />

                 <Input
                   type="number"
                   placeholder="Meal duration"
                   value={mealtoAdd.duration}
                   onChange={(e) =>
                     setmealtoAdd({ duration: e.target.value })
                   }
                 />
                 <br />

                 <label>Meal discount</label>
                 <br />
                 <br />

                 <Input
                   type="number"
                   placeholder="Meal discount"
                   value={mealtoAdd.discount}
                   onChange={(e) =>
                     setmealtoAdd({ discount: e.target.value })
                   }
                 />
                 <div className="row">
                   <div className="col-3">
                     {" "}
                     <button className="button col-12" onClick={DoaddMeal}>
                       submit
                     </button>
                   </div>
                   <div className="col-3">
                     <button
                       className="button col-12"
                       onClick={doCloseAddMeal}
                     >
                       cancel
                     </button>
                   </div>
                 </div>
               </div>
                  ) : (
                    <div></div>
                  )
                  
                  }
    </div>
    
  )

};

export default Resto;
