import React from "react";

import axios from "axios";
import { BaseUrl } from "../../helpers/base_url";
import Table from "../../components/table/Table";
import socketIOClient from "socket.io-client";
import { useState } from "react";
const SERVER = "http://127.0.0.1:8080";

const Orders = () => {
  const [listOrder, setlistOrder] = useState([]);
  const [dataReady, setdataReady] = useState(false);
  const [newOrdersList, setnewOrdersList] = useState([]);
  const customerTableHead = ["date", "note", "price", "meal number", ""];
  const renderHead = (item, index) => <th key={index}>{item}</th>;
  const renderOrderBody = (item, index) => (
    <tr key={index}>
      <td className=" col-4">{item.createdAt}</td>

      <td className=" col-2">{item.note}</td>

      <td className=" col-2">{item.price}</td>

      <td className=" col-2">{item.meals.length}</td>
    </tr>
  );
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/command/getall/byrestaurantid/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        //   console.log(response.data);
        setlistOrder(response.data);
        setdataReady(true);
      });
  }, []);
  //renderNewOrder = (item) => {};
  var socket = socketIOClient(SERVER);
  socket.on("connection", () => {
    socket.emit("restaurantI", localStorage.getItem("restaurant_id"));
    socket.on("newcommand", (command) => {
      if (command.restaurant == localStorage.getItem("restaurant_id")) {
        newOrdersList.push();
      }
    });
  });
  return (
    <div className="col-12">
      <h2 className="page-header">Orders</h2>
      <br />
      <div className="card col-8">
        <div className="card__body">
          <div className="row">
            <div className="col-4">
              {" "}
              <h3>New Order</h3>
            </div>
            <div className="col-4">
              <button className="button col-6">Accept</button>
              <button className="button col-6">Cancel</button>
            </div>
          </div>
          <br />
          <h4>Note :</h4>
          <p>n7ebha maghyr slata mechweya</p>
          <h4>Meal List :</h4>

          <p>Makloub </p>
          <p>Makloub </p>
          <p>Makloub </p>
          <h4>25Dt</h4>
        </div>
      </div>
      <div className="col-12">
        <div className="row">
          <div className="col-12">
            <div className="card">
              <div className="card__body">
                {dataReady ? (
                  <Table
                    limit="10"
                    headData={customerTableHead}
                    renderHead={(item, index) => renderHead(item, index)}
                    bodyData={listOrder}
                    renderBody={(item, index) => renderOrderBody(item, index)}
                  />
                ) : (
                  <div></div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Orders;
