import React from "react";
import axios from "axios";
import { BaseUrl } from "../../helpers/base_url";
import { useState } from "react";
import { Input } from "../Login/LoginElements";

import GoogleMapReact from 'google-map-react';
const AnyReactComponent = ({ text }) => <div>{text}</div>;
const Resto = () => {
  const data = new FormData() 
  const [openupdate, setopenupdate] = useState(false);
  const [imgUpdate, setimgUpdate]= useState({});
  const [openEdit, setopenEdit] = useState(false);
  const [dataReady, setdataReady] = useState(false);
  const [restoData,setrestoData] = useState({
    name : "",
    description : "",
    location : [],
    speciality : "",
    image : ""
  });
  const openEditRestofore = async (restaurant) => {
    setopenEdit(false);
    await setrestoData(restaurant);
    setopenEdit(true);
  };
  
  const UpdateRestau = async ()  => {
    
    data.append('file',imgUpdate)
    data.append('name',restoData.name)
    data.append('description',restoData.description)
    data.append('speciality',restoData.speciality)
    console.log("form data ",data.get('file')) ;
    await axios
      .post(
        BaseUrl + "/restaurant/update",
            data,
        {
          headers: {
            restaurantid: localStorage.getItem("restaurant_id"),
          },
        }
      )
      .then(async (res) => {
        if (res.status == 201) {
          doCloseupdate();
          setrestoData({});
          setopenEdit(false);
          //await setrestoData((restoData) => [...restoData, res.data]);
          setopenEdit(true);
        }
      });
  };
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/restaurant/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        //   console.log(response.data);
        if(response.status == 200)
        {
          setdataReady(true);
          setrestoData(response.data);
        }
        

      }).catch((error) => {
        console.log(error);
      });
  }, []);
  const doCloseupdate= () => {
    console.log("aaa");
    setopenEdit(false);
  };

  return(
    <div>
       {dataReady ? (
                
                 <div>
                   <button onClick={() => openEditRestofore(restoData)} className="button col-2">Edit</button>
                   <div> { restoData.name}  </div>
                   <br></br>
                   <div> { restoData.description} </div>
                   <br></br>
                   <div> { restoData.speciality}  </div>
                   <br></br>
                  <img src={ restoData.image}  style={{height:'auto',width:'35%'}} />
                  <br></br>
                    
                     </div>
                    

                ) : (
                  <div></div>
                )}
               {openEdit ? (
                 <div className="col-12">
                    <br />

                    <label>Restaurant name</label>
                    <br />
                    <br />
                    <Input
                      type="text"
                      placeholder="Restaurant name"
                      value={restoData["name"]}
                      onChange={(e) => setrestoData({ name: e.target.value })}
                    />
                    <br />

                    <label>Restaurant description</label>
                    <br />
                    <br />
                    <Input
                      type="text"
                      placeholder="Restaurant description"
                      value={restoData.description}
                      onChange={(e) =>
                        setrestoData({ description: e.target.value })
                      }
                    />

                    <label>
                      <br />

                      <label>Restaurant speciality</label>
                      <br />
                      <br />
                      <Input
                        type="text"
                        placeholder="Restaurant speciality"
                        value={restoData.speciality}
                        onChange={(e) =>
                          setrestoData({ speciality: e.target.value })
                        }
                      />
                    </label>
                    <br />
                    <label>Restaurant picture
                    <br />
                    <br />
                    <input type="file" name="file" onChange={
                     
                      (e) =>
                      {
                        console.log("file",e.target.files[0])
                        setimgUpdate(e.target.files[0] )
                      }
                      
                        }/>
                    </label>
                    <div className="row">
                      <div className="col-3">
                        {" "}
                        <button className="button col-12" onClick={UpdateRestau}>
                          Update
                        </button>
                      </div>
                      <div className="col-3">
                        <button
                          className="button col-12"
                          onClick={doCloseupdate}
                        >
                          cancel
                        </button>
                      </div>
                    </div>
                  </div>
                  ) : (
                    <div></div>
                  )
                  
                  }
    </div>
    
  )

};

export default Resto;
