import React from "react";
import axios from "axios";
import { BaseUrl } from "../../helpers/base_url";
import Table from "../../components/table/Table";
import { useState } from "react";
const SERVER = "http://127.0.0.1:8080";

const Resto = () => {
  const [listOrder, setlistOrder] = useState([]);
  const [dataReady, setdataReady] = useState(false);
  const customerTableHead = ["name", "description", "image", "speciality", ""];
  const renderHead = (item, index) => <th key={index}>{item}</th>;
  const renderOrderBody = (item, index) => (
    <tr key={index}>
      <td className=" col-4">{item.name}</td>

      <td className=" col-2">{item.description}</td>

      <td className=" col-2">{item.image}</td>

      <td className=" col-2">{item.meals.speciality}</td>
    </tr>
  );
  React.useEffect(async () => {
    await axios
      .get(
        BaseUrl +
          "/restaurant/" +
          localStorage.getItem("restaurant_id")
      )
      .then((response) => {
        //   console.log(response.data);
        setlistOrder(response.data);
        setdataReady(true);
      });
  }, []);
};

export default Resto;
