import React,{useState} from 'react'
import Table from '../../components/table/Table'
import {Input} from '../Login/LoginElements'
import axios from "axios";
import { BaseUrl } from '../../helpers/base_url';
const CategorieTableHead = [
    '',
    'Categorie'
]


const renderHead = (item, index) => <th key={index}>{item}</th>
const renderBody = (item, index) => (
    <tr key={index}>
        <td>{item.id}</td>
        <td>{item.name}</td>
    </tr>
)
const Menu = () => {
 
    const [openAddCategorie,setopenAddCategorie] = useState(false);
    const [listCategories,setlistCategories]=useState([]);
    React.useEffect((useState)=>{
        axios.get(BaseUrl+"/menu/getbyrestaurant/6212cec87c3cf81fcfae25cf").then((response)=>{
            console.log(response.data['categories'])
            response.data['categories'].forEach((item)=>{
              
                setlistCategories(arr=>[...arr,'${"name":'+item["name"]+'}'])
                console.log(item["name"])
            })
            console.log("aa")
            console.log(listCategories)
        }).catch((error)=>{
            console.log(error)
        })
    },[]);
    const doOpenAddCategorie=()=> {
        setopenAddCategorie(true)
    }
    const doCloseopenAddCategorie=()=>{
        setopenAddCategorie(false)
    }

    return (
        <div>
            <h2 className="page-header">
                Menu
            </h2>
            <div className='row'><h3 className="page-header col-3">Categories list</h3>
            <button className='button col-2'onClick={doOpenAddCategorie}>
                Add Categorie
            </button>
            </div>
            {openAddCategorie? (
                    <div className='row'>
                    <div className="col-6">
                        <div className="card">
                        <form className='form'>
                            <label>Categorie name</label>
                            <br/>
                            <Input
                                    type="text"
                                    placeholder='Categorie'
                                />           
                                <button className='button col-2' >submit</button>
       
                          </form>
                        <button className='button col-2' onClick={doCloseopenAddCategorie}>cancel</button>
                        </div>
                    
                    </div>
                    </div>

            ):(
                <div></div>
            )}


            <div className="row">
                <div className="col-6">
                    <div className="card">
                        <div className="card__body">
                            <Table
                                limit='10'
                                headData={CategorieTableHead}
                                renderHead={(item, index) => renderHead(item, index)}
                                bodyData={listCategories}
                                renderBody={(item, index) => renderBody(item, index)}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Menu
